//   type: "phone",
//   images: [
//     {
//       nameImage: "Xanh",
//       urls: "https://res.cloudinary.com/dl6y9cyqb/image/upload/v1682272442/sg3ippzaqpi9imkzku0p.jpg",
//     },
//     {
//       nameImage: "Đen",
