//     },
//   ],

//   title: "Chiếc ddiejn thoại tuyệt vời",
//   content: "Chiếc ddiejn thoại tuyệt vời lắm",
//   cpu: "ádv",
//   ram: "sadv",
//   rom: "ádv",
//   graphicCard: "",
//   battery: "ádv",
