      setName(data.name);
    }
    if (data.manufacture) {
      setManufacture(data.manufacture);
    }
    if (data.type) {
      setType(data.type);
    }
    // if (data.images) {
    //   var link = [];
    //   for (var i = 0; i < data.images.length; i++) {
    //     link.push(data.images[i].urls);
    //   }
    //   setFile(link);

    //   var l = [];
    //   for (var i = 0; i < data.images.length; i++) {
    //     l.push(data.images[i].nameImage);
    //   }
    //   setDescriptionImage(l);
    // }

